import React, {useState} from "react";
import Modal from "../../Components/Modal";
import SignUp from "../SignUp/SignUp";
import LogIn from "../LogIn/LogIn";

function Main(){
    const [modalOpen, setModalOpen] = useState(false);
    const [loginModal,setLoginModal]=useState(false);

    const openModal = (e) => {
        setModalOpen(true);
        if(e.header==="Login"){
            alert(e.header);
            setLoginModal(true);
        }
        else{
            setLoginModal(false);
        }
    };
    const closeModal = () => {
        setModalOpen(false);
    };
    // const JoinBtn = () =>{}

    return(
        <div>
            main_page
            <button onClick={openModal} header="Login">Login</button>
            <button onClick={openModal}>Join</button>
            {loginModal?<Modal open={modalOpen} close={closeModal} header="Login" component={<LogIn />} />
                :<Modal open={modalOpen} close={closeModal} header="Join" component={<SignUp />} />}

                {/*// Modal/index.js <main> {props.children} </main>에 내용이 입력된다. 리액트 함수형 모달*/}


        </div>

    );
};

export default Main;
