import React, {useState} from "react";
import Modal from "../../Components/Modal";
import SignUp from "../SignUp/SignUp";
import LogIn from "../LogIn/LogIn";

function Main(){
    const [modalOpen, setModalOpen] = useState(false);
    const [loginModal,setLoginModal]=useState(false);
    const [loginComponent,setLoginComponent]=useState(<LogIn/>);

    const openModal = () => {
        setModalOpen(true);
        if(loginModal===false){
            setLoginComponent(<SignUp />);
        }
    };
    const closeModal = () => {
        setModalOpen(false);
        setLoginModal(false);
    };
    const onChangeLoginModal = () =>{
        setLoginModal(true);
    }

    return(
        <div>
            main_page
            <button onClick={openModal} onChange={onChangeLoginModal}>Login</button>
            <button onClick={openModal}>Join</button>
            {loginModal?<Modal open={modalOpen} close={closeModal} header="Login" component={loginComponent} />
                :<Modal open={modalOpen} close={closeModal} header="Join" component={loginComponent} />}

                {/*// Modal/index.js <main> {props.children} </main>에 내용이 입력된다. 리액트 함수형 모달*/}


        </div>

    );
};

export default Main;
